mapper.select:
select * from(
	select rownum as lot, tmp.* from(
		select * from auction_view
	)tmp 
)where no=4 artist like '%'||#{artist}||'%' or title like '%'||#{title}||'%' or lot=#{lot}

select rownum as rn, tmp.* from(
	select * from auction_main where to_date(auction_start, 'YY/MM/DD HH24:MI')>sysdate
)tmp order by rn desc


	select rownum as rn, tmp.* from(
		select * from auction_main where sysdate>to_date(auction_end, 'YY/MM/DD HH24:MI')
	)tmp
